package Lecture56;

public class LeftShiftAndRightShift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 7;
		System.out.println(a<<1);// Left Shift Once
		System.out.println(a<<2);// Left Shift Twice
		System.out.println(a<<3);//Left Shift Thrice
		
		System.out.println("....");
		System.out.println(a>>1);// Left Shift Once
		System.out.println(a>>2);// Left Shift Twice
		System.out.println(a>>3);//Left Shift Thrice
		
		int b = -7;
		System.out.println(b<<1);// Left Shift Once
		System.out.println(b<<2);// Left Shift Twice
		System.out.println(b<<3);//Left Shift Thrice
		System.out.println("......");
		System.out.println(b>>1);// Left Shift Once
		System.out.println(b>>2);// Left Shift Twice
		System.out.println(b>>3);//Left Shift Thrice
		
		
	}

}
